mapper.select:
SELECT ( SELECT COUNT (1) FROM Pro_Product p, Ord_OrderInfo o, Pro_Tour pt
,H5_ShopComments hs
WHERE pt.id = o.TourID AND p.id = pt.ProID
AND hs.OrderId = o.ID
and o.DataStatus = #{dataStatus}
AND o.CreatedByID = #{createId}
AND (p.AliaseName LIKE '%${condition}%' OR o.OrderNo LIKE '%${condition}%' OR p.ProNo LIKE '%${condition}%' )
) AS totalNum, o.ID as ordId, p.id AS proId, pt.ID AS proTourId, p.AliaseName AS aliaseName, o.OrderNo AS orderNo, o.TourDate AS tourDate, o.AdultNum AS adultNum, o.ChildNum AS childNum, p.TourDays AS tourDays, o.PayableAmt AS payableAmt, p.CoverImg AS coverImg, o.ReturnDate as returnDate, o.DataStatus as dataStatus, o.SupplierID as supplierID FROM Pro_Product p, Ord_OrderInfo o, Pro_Tour pt
,H5_ShopComments hs
WHERE pt.id = o.TourID AND p.id = pt.ProID
AND hs.OrderId = o.ID
and o.DataStatus = #{dataStatus}
AND o.CreatedByID = #{createId}
AND (p.AliaseName LIKE '%${condition}%' OR o.OrderNo LIKE '%${condition}%' OR p.ProNo LIKE '%${condition}%' )
ORDER BY o.CreatedDate DESC OFFSET #{startPage} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT a.ID AS ordId , --总条数begin (SELECT count(*) FROM Ord_OrderInfo AS a LEFT JOIN Volunteer_Commission_Record as r on a.id = r.OrderID LEFT JOIN Pro_Product AS c ON a.RouteID = c.ID --包含待确认的已收款 LEFT JOIN ( SELECT OrderID , SUM(a.CollectAmt) CollectAmt , OrgID FROM Ord_Order_Collection a WHERE a.CollectStatus != 3 AND ISNULL(a.Isbuyerapply,0)!=1 GROUP BY OrderID , a.OrgID ) AS d ON a.ID = d.OrderID AND a.OrgID = d.OrgID --已经付款 LEFT JOIN ( SELECT OrderID , OrgID , SUM(a.Amount) Amount FROM dbo.Fin_OrderCostPaid a WHERE a.DataStatus = 2 GROUP BY a.OrderID , a.OrgID ) AS e ON a.ID = e.OrderID AND a.OrgID = e.OrgID LEFT JOIN ( SELECT SUM(a.InvoiceAmt) InvoiceAmt , a.OrderID FROM [Ord_Order_Invoice_Item] AS a LEFT JOIN Ord_Order_Invoice AS b ON a.InvoiceID = b.ID WHERE b.InvoiceStatus = 2 GROUP BY a.OrderID ) AS invoce ON invoce.OrderID = a.ID WHERE 1=1
) AS totalNum , --总条数end a.RouteID AS proId , a.TourID AS proTourId , a.OrderName AS aliaseName, a.TourDate AS tourDate , c.TourDays AS tourDays , c.coverImg AS coverImg , a.ReturnDate AS returnDate , a.DestName AS destName , a.OrderNo AS orderNo, a.Contact AS contact , a.Mobile AS mobile , a.ReceivableAmt , a.PayableAmt AS payableAmt, ISNULL(r.CommissionAmt, 0) as commissionAmt, a.AdultNum AS adultNum, a.ChildNum AS childNum, a.CreatedDate AS createdDate, a.DataStatus AS dataStatus , ISNULL(e.Amount, 0) AS PaidAmount, a.SupplierName AS supplierName, a.SupplierID AS supplierID FROM Ord_OrderInfo AS a LEFT JOIN Volunteer_Commission_Record as r on a.id = r.OrderID LEFT JOIN Pro_Product AS c ON a.RouteID = c.ID --包含待确认的已收款 LEFT JOIN ( SELECT OrderID , SUM(a.CollectAmt) CollectAmt , OrgID FROM Ord_Order_Collection a WHERE a.CollectStatus != 3 AND ISNULL(a.Isbuyerapply,0)!=1 GROUP BY OrderID , a.OrgID ) AS d ON a.ID = d.OrderID AND a.OrgID = d.OrgID --已经付款 LEFT JOIN ( SELECT OrderID , OrgID , SUM(a.Amount) Amount FROM dbo.Fin_OrderCostPaid a WHERE a.DataStatus = 2 GROUP BY a.OrderID , a.OrgID ) AS e ON a.ID = e.OrderID AND a.OrgID = e.OrgID LEFT JOIN ( SELECT SUM(a.InvoiceAmt) InvoiceAmt , a.OrderID FROM Ord_Order_Invoice_Item AS a LEFT JOIN Ord_Order_Invoice AS b ON a.InvoiceID = b.ID WHERE b.InvoiceStatus = 2 GROUP BY a.OrderID ) AS invoce ON invoce.OrderID = a.ID WHERE 1=1
order BY a.RevisedDate desc

mapper.update:
UPDATE Ord_OrderInfo
SET
PayableAmt = #{payableAmt}
WHERE
ID = #{orderId}

mapper.select:
SELECT DataStatus as dataStatus, COUNT (*) as orderNum FROM Ord_OrderInfo WHERE 1=1
AND (OrderName LIKE '%${condition}%' or OrderNo LIKE '%${condition}%' )
AND OrderSalerID = #{userId} AND SupplierID = #{orgId}
AND CreatedByID = #{userId}
GROUP BY DataStatus
