mapper.select:
SELECT * from (SELECT t.ID, t.orgId as orgId, 0 as isOutgoing, '00000000-0000-0000-0000-000000000000' as directSalesSendId, t.AliaseName, t.TravelName AS brandName, t.StarLevel, t.ProTag, t.TourDays, t.Departure, t.Destination, t.ProCategoryID, t.ProType, t.ProTypeId, t.ProBrand, (SELECT MIN(DirectPrice) from Pro_Tour_PricePolicy x LEFT JOIN Pro_Tour y on x.tourId = y.id where x.ProID = t.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as DirectPrice, (SELECT MIN(SalesPrice) from Pro_Tour_PricePolicy x LEFT JOIN Pro_Tour y on x.tourId = y.id where x.ProID = t.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as SalesPrice, (SELECT MIN(RebatePrice) from Pro_Tour_PricePolicy x LEFT JOIN Pro_Tour y on x.tourId = y.id where x.ProID = t.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as RebatePrice, RIGHT(CONVERT(varchar(100), t.CreatedDate, 14),3) AS salesQuantity, (SELECT tours = ( stuff( ( SELECT top 5 ',' + CONVERT (VARCHAR(100), TourDate, 11) FROM Pro_Tour WHERE ProID = a.ProID and DueDate > DATEADD(DAY ,- 1, GETDATE()) ORDER BY TourDate FOR xml path ('') ), 1, 1, '' ) ) FROM Pro_Tour AS a WHERE a.ProID = t.id GROUP BY a.ProID ) as tours, t.CoverImg, t.CreatedDate from Pro_Product as t LEFT JOIN Pro_Product_Scope AS ts ON t.ID = ts.proID LEFT JOIN pro_product_site AS t1 ON t.ID = t1.proID -- JOIN ( SELECT a.proId FROM Pro_Tour AS a JOIN Pro_Tour_PricePolicy AS b ON a.ID = b.TourID where a.OrgID = #{orgId} AND b.SiteID = #{siteId} AND a.TourStatus = 1 --
AND
TourDate LIKE '%${item}%'
AND TourDate >= #{beginDate}
AND TourDate <= #{endDate}
GROUP BY a.proId ) AS TQ ON TQ.proId = t.id WHERE 1=1 AND t1.SiteId = #{siteId} AND t.DataStatus = 2 -- 123 AND t.SalesType in(1,3) AND t.AuditStatus = 1 AND ts.Scope = 2 AND t.orgId = #{orgId}
UNION
SELECT p.ID, z.ReceiverID as orgId, 1 as isOutgoing, z.SupplierID as directSalesSendId, p.AliaseName, p.TravelName AS brandName, p.StarLevel, p.ProTag, p.TourDays, p.Departure, p.Destination, p.ProCategoryID, p.ProType, p.ProTypeId, p.ProBrand, (SELECT MIN(DirectPrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as DirectPrice, (SELECT MIN(SalesPrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as SalesPrice, (SELECT MIN(RebatePrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as RebatePrice, RIGHT(CONVERT(varchar(100), p.CreatedDate, 14),3) AS salesQuantity, (SELECT tours = ( stuff( ( SELECT top 5 ',' + CONVERT (VARCHAR(100), TourDate, 11) FROM Pro_Product_DirectSales WHERE ProID = a.ProID and DueDate > DATEADD(DAY ,- 1, GETDATE()) ORDER BY TourDate FOR xml path ('') ), 1, 1, '' ) ) FROM Pro_Product_DirectSales AS a WHERE a.ProID = p.id GROUP BY a.ProID ) as tours, CoverImg, p.CreatedDate FROM Pro_Product AS p LEFT JOIN pro_product_site AS s ON p.ID = s.proID LEFT JOIN Pro_Product_DirectSales AS z ON p.ID = z.proID LEFT JOIN Pro_Product_Scope AS ts ON p.ID = ts.proID WHERE 1=1 AND s.SiteId = #{siteId} AND z.ReceiverID = #{orgId} AND ts.Scope = 2 AND z.DataStatus = 1 AND z.SupplierDataStatus = 1 AND z.AuditStatus = 1 AND Z.DueDate > DATEADD(DAY ,- 1, GETDATE()) --
AND
z.TourDate LIKE '%${item}%'
AND z.TourDate >= #{beginDate}
AND z.TourDate <= #{endDate}
)as b WHERE 1=1

