SELECT p.ID, z.ReceiverID as orgId, 1 as isOutgoing, z.SupplierID as directSalesSendId, p.AliaseName, p.TravelName AS brandName, p.StarLevel, p.ProTag, p.TourDays, p.Departure, p.Destination, p.ProCategoryID, p.ProType, p.ProTypeId, p.ProBrand, (SELECT MIN(DirectPrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as DirectPrice, (SELECT MIN(SalesPrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as SalesPrice, (SELECT MIN(RebatePrice) from Pro_Product_DirectSales_Item x LEFT JOIN Pro_Product_DirectSales y ON x.DirectSalesID=y.id where x.ProID = p.ID AND x.IsDefault = 1 AND x.SiteID = #{siteId} AND ReceiverID = #{orgId} AND y.DueDate> DATEADD(DAY ,- 1, GETDATE())) as RebatePrice, RIGHT(CONVERT(varchar(100), p.CreatedDate, 14),3) AS salesQuantity, (SELECT tours = ( stuff( ( SELECT top 5 ',' + CONVERT (VARCHAR(100), TourDate, 11) FROM Pro_Product_DirectSales WHERE ProID = a.ProID and DueDate > DATEADD(DAY ,- 1, GETDATE()) ORDER BY TourDate FOR xml path ('') ), 1, 1, '' ) ) FROM Pro_Product_DirectSales AS a WHERE a.ProID = p.id GROUP BY a.ProID ) as tours, CoverImg, p.CreatedDate FROM Pro_Product AS p LEFT JOIN pro_product_site AS s ON p.ID = s.proID LEFT JOIN Pro_Product_DirectSales AS z ON p.ID = z.proID LEFT JOIN Pro_Product_Scope AS ts ON p.ID = ts.proID WHERE 1=1 AND s.SiteId = #{siteId} AND z.ReceiverID = #{orgId} AND ts.Scope = 2 AND z.DataStatus = 1 AND z.SupplierDataStatus = 1 AND z.AuditStatus = 1 AND Z.DueDate > DATEADD(DAY ,- 1, GETDATE()) -- )as b WHERE 1=1 AND b.tours is not null
